import React, { useReducer } from "react";
import './App.css';

import CountingButtons from "./Components/CountingButtons/CountingButtons.tsx";
import Counter from "./Components/Counter/Counter.tsx";

type ButtonAction = "Increment" | "Decrement";

const countReducer = (state: number, action: ButtonAction) => {
  console.log(action, "ac tion");
  switch (action) {
    case "Increment":
      return state + 1;
    case "Decrement":
      return state - 1;
    default:
      return state;
  }
}

const App = () => {
  const [count, setCount] = useReducer(countReducer, 0);

  return (
    <>
      <p>There is nothing important here yet! Here is a cool counter while you wait.</p>
      <Counter count={count} />
      <CountingButtons setCount={setCount} />
    </>
  );
}

export default App;
export { ButtonAction };
